package breakstatements;

// int a=1; = uses when we assign variable
// == uses to compare the exact value
public class asdfuijyhhj {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int x=1; x<=10; x++) {
			if(x==7) {
				break;
			}
			System.out.println(x);
		}
	}

}
